import React from 'react';
import { NavLink, NavLinkProps } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(() => ({
    navLink: {
        textDecoration: 'none',
        color: '#333',
        padding: '0.5rem',
        margin: '0 0.5rem',
        fontSize: '1.5rem',
        fontFamily: 'Cormorant Garamond, sans-serif',
        transition: 'color 0.2s',
        '&:hover': {
            color: '#888',
        },
        '&.active': {
            color: '#ff5722',
            fontWeight: 'bold',
        },
    },
}));

interface TopNavItemProps extends NavLinkProps {
    text: string;
    href: string;
}

const TopNavItem: React.FC<TopNavItemProps> = ({ to, text, ...rest }) => {
    const classes = useStyles();

    return (
        <NavLink to={to} className={classes.navLink} {...rest}>
            {text}
        </NavLink>
    );
};

export default TopNavItem;
